mapper.insert:
INSERT INTO playLog
(
    playNo,
    log
)
VALUES
(
    #{playNo},
    #{log}
)

mapper.select:
SELECT gs.nm, gs.cd, IFNULL(gs.rmrk, '') AS rmrk FROM game g, gameSttng gs WHERE g.gameNo = gs.gameNo AND g.gameNo = #{gameNo}
AND g.sttng1GrpCd = gs.grpCd AND g.sttng1GrpCd = #{grpCd}
AND g.sttng2GrpCd = gs.grpCd AND g.sttng2GrpCd = #{grpCd}
AND g.sttng3GrpCd = gs.grpCd AND g.sttng3GrpCd = #{grpCd}
ORDER BY gs.ordr
